BrawSimResults <- if (requireNamespace("jmvcore", quietly=TRUE)) R6::R6Class(
    "BrawSimResults",
    inherit = jmvcore::Group,
    active = list(
        graphPlot = function() private$.items[["graphPlot"]],
        reportPlot = function() private$.items[["reportPlot"]],
        debug = function() private$.items[["debug"]],
        sendSample = function() private$.items[["sendSample"]],
        sendMultiple = function() private$.items[["sendMultiple"]]),
    private = list(),
    public=list(
        initialize=function(options) {
            super$initialize(
                options=options,
                name="",
                title="BrawStats:Simulate Data",
                refs="brawstats book")
            self$add(jmvcore::Image$new(
                options=options,
                name="graphPlot",
                title=" ",
                width=600,
                height=360,
                visible=TRUE,
                renderFun=".plotGraph"))
            self$add(jmvcore::Image$new(
                options=options,
                name="reportPlot",
                title=" ",
                width=600,
                height=200,
                visible=TRUE,
                refs=list(
                    "brawstats",
                    "book"),
                renderFun=".plotReport"))
            self$add(jmvcore::Preformatted$new(
                options=options,
                name="debug",
                visible=FALSE))
            self$add(jmvcore::Output$new(
                options=options,
                name="sendSample",
                measureType="nominal",
                varTitle="Sample",
                items=3,
                clearWith=list(
                    "makeSampleBtn")))
            self$add(jmvcore::Output$new(
                options=options,
                name="sendMultiple",
                measureType="continuous",
                varTitle="Mult",
                items=3,
                clearWith=list(
                    "makeMultipleBtn")))}))

BrawSimBase <- if (requireNamespace("jmvcore", quietly=TRUE)) R6::R6Class(
    "BrawSimBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = "BrawStats",
                name = "BrawSim",
                version = c(1,0,0),
                options = options,
                results = BrawSimResults$new(options=options),
                data = data,
                datasetId = datasetId,
                analysisId = analysisId,
                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE,
                requiresMissings = FALSE,
                weightsSupport = 'na')
        }))

#' BrawStats:Simulate Data
#'
#' 
#' @param DVname .
#' @param DVtype .
#' @param DVmu .
#' @param DVsd .
#' @param DVskew .
#' @param DVkurt .
#' @param DVnlevs .
#' @param DViqr .
#' @param DVncats .
#' @param DVprops .
#' @param IVname .
#' @param IVtype .
#' @param IVmu .
#' @param IVsd .
#' @param IVskew .
#' @param IVkurt .
#' @param IVnlevs .
#' @param IViqr .
#' @param IVncats .
#' @param IVprops .
#' @param IV2on .
#' @param IV2name .
#' @param IV2type .
#' @param IV2mu .
#' @param IV2sd .
#' @param IV2skew .
#' @param IV2kurt .
#' @param IV2nlevs .
#' @param IV2iqr .
#' @param IV2ncats .
#' @param IV2props .
#' @param EffectSize1 .
#' @param EffectSize2 .
#' @param EffectSize3 .
#' @param EffectSize12 .
#' @param Heteroscedasticity .
#' @param Residuals .
#' @param WorldOn .
#' @param WorldPDF .
#' @param WorldRZ .
#' @param Worldk .
#' @param WorldNullP .
#' @param SampleSize .
#' @param SampleSpread .
#' @param SampleGamma .
#' @param SampleMethod .
#' @param SampleUsage1 .
#' @param SampleUsage2 .
#' @param Dependence .
#' @param Outliers .
#' @param Cheating .
#' @param CheatingAttempts .
#' @param ReplicationOn .
#' @param ReplicationPower .
#' @param ReplicationPrior .
#' @param ReplicationAttempts .
#' @param ReplicationDecision .
#' @param ReplicationAlpha .
#' @param alphaSig .
#' @param equalVar .
#' @param Transform .
#' @param showHypothesisBtn .
#' @param makeSampleBtn .
#' @param numberSamples .
#' @param makeMultipleBtn .
#' @param singleVar1 .
#' @param singleVar2 .
#' @param multipleVar1 .
#' @param multipleVar2 .
#' @param showSampleType .
#' @param showInferParam .
#' @param showMultipleParam .
#' @param whichShowMultiple .
#' @param exploreNPoints .
#' @param exploreMinVal .
#' @param exploreMaxVal .
#' @param exploreXLog .
#' @param numberExplores .
#' @param makeExploreBtn .
#' @param typeExplore .
#' @param showExploreParam .
#' @param exploreVar1 .
#' @param exploreVar2 .
#' @param whichShowExplore .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$graphPlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$reportPlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$debug} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$sendSample} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$sendMultiple} \tab \tab \tab \tab \tab an output \cr
#' }
#'
#' @export
BrawSim <- function(
    DVname = "DV",
    DVtype = "Interval",
    DVmu = 0,
    DVsd = 1,
    DVskew = 0,
    DVkurt = 3,
    DVnlevs = 7,
    DViqr = 4,
    DVncats = 2,
    DVprops = "1,1",
    IVname = "IV",
    IVtype = "Interval",
    IVmu = 0,
    IVsd = 1,
    IVskew = 0,
    IVkurt = 3,
    IVnlevs = 7,
    IViqr = 4,
    IVncats = 2,
    IVprops = "1,1",
    IV2on = FALSE,
    IV2name = "IV2",
    IV2type = "Interval",
    IV2mu = 0,
    IV2sd = 1,
    IV2skew = 0,
    IV2kurt = 3,
    IV2nlevs = 7,
    IV2iqr = 4,
    IV2ncats = 2,
    IV2props = "1,1",
    EffectSize1 = 0,
    EffectSize2 = 0,
    EffectSize3 = 0,
    EffectSize12 = 0,
    Heteroscedasticity = 0,
    Residuals = "normal",
    WorldOn = FALSE,
    WorldPDF = "Single",
    WorldRZ,
    Worldk = 0.3,
    WorldNullP = 0.5,
    SampleSize = 42,
    SampleSpread = "no",
    SampleGamma = 1.56,
    SampleMethod = "Random",
    SampleUsage1 = "Between",
    SampleUsage2 = "Between",
    Dependence = 0,
    Outliers = 0,
    Cheating = "None",
    CheatingAttempts = 5,
    ReplicationOn = FALSE,
    ReplicationPower = 0.8,
    ReplicationPrior = "None",
    ReplicationAttempts = 1,
    ReplicationDecision = "Cautious",
    ReplicationAlpha = 0.05,
    alphaSig = 0.05,
    equalVar = "yes",
    Transform = "None",
    showHypothesisBtn,
    makeSampleBtn = FALSE,
    numberSamples = 100,
    makeMultipleBtn,
    singleVar1 = "r",
    singleVar2 = "p",
    multipleVar1 = "r",
    multipleVar2 = "p",
    showSampleType = "Sample",
    showInferParam = "Basic",
    showMultipleParam = "Basic",
    whichShowMultiple = "all",
    exploreNPoints = 13,
    exploreMinVal = 10,
    exploreMaxVal = 250,
    exploreXLog = FALSE,
    numberExplores = 10,
    makeExploreBtn,
    typeExplore,
    showExploreParam = "Basic",
    exploreVar1 = "r",
    exploreVar2 = "p",
    whichShowExplore = "all") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("BrawSim requires jmvcore to be installed (restart may be required)")


    options <- BrawSimOptions$new(
        DVname = DVname,
        DVtype = DVtype,
        DVmu = DVmu,
        DVsd = DVsd,
        DVskew = DVskew,
        DVkurt = DVkurt,
        DVnlevs = DVnlevs,
        DViqr = DViqr,
        DVncats = DVncats,
        DVprops = DVprops,
        IVname = IVname,
        IVtype = IVtype,
        IVmu = IVmu,
        IVsd = IVsd,
        IVskew = IVskew,
        IVkurt = IVkurt,
        IVnlevs = IVnlevs,
        IViqr = IViqr,
        IVncats = IVncats,
        IVprops = IVprops,
        IV2on = IV2on,
        IV2name = IV2name,
        IV2type = IV2type,
        IV2mu = IV2mu,
        IV2sd = IV2sd,
        IV2skew = IV2skew,
        IV2kurt = IV2kurt,
        IV2nlevs = IV2nlevs,
        IV2iqr = IV2iqr,
        IV2ncats = IV2ncats,
        IV2props = IV2props,
        EffectSize1 = EffectSize1,
        EffectSize2 = EffectSize2,
        EffectSize3 = EffectSize3,
        EffectSize12 = EffectSize12,
        Heteroscedasticity = Heteroscedasticity,
        Residuals = Residuals,
        WorldOn = WorldOn,
        WorldPDF = WorldPDF,
        WorldRZ = WorldRZ,
        Worldk = Worldk,
        WorldNullP = WorldNullP,
        SampleSize = SampleSize,
        SampleSpread = SampleSpread,
        SampleGamma = SampleGamma,
        SampleMethod = SampleMethod,
        SampleUsage1 = SampleUsage1,
        SampleUsage2 = SampleUsage2,
        Dependence = Dependence,
        Outliers = Outliers,
        Cheating = Cheating,
        CheatingAttempts = CheatingAttempts,
        ReplicationOn = ReplicationOn,
        ReplicationPower = ReplicationPower,
        ReplicationPrior = ReplicationPrior,
        ReplicationAttempts = ReplicationAttempts,
        ReplicationDecision = ReplicationDecision,
        ReplicationAlpha = ReplicationAlpha,
        alphaSig = alphaSig,
        equalVar = equalVar,
        Transform = Transform,
        showHypothesisBtn = showHypothesisBtn,
        makeSampleBtn = makeSampleBtn,
        numberSamples = numberSamples,
        makeMultipleBtn = makeMultipleBtn,
        singleVar1 = singleVar1,
        singleVar2 = singleVar2,
        multipleVar1 = multipleVar1,
        multipleVar2 = multipleVar2,
        showSampleType = showSampleType,
        showInferParam = showInferParam,
        showMultipleParam = showMultipleParam,
        whichShowMultiple = whichShowMultiple,
        exploreNPoints = exploreNPoints,
        exploreMinVal = exploreMinVal,
        exploreMaxVal = exploreMaxVal,
        exploreXLog = exploreXLog,
        numberExplores = numberExplores,
        makeExploreBtn = makeExploreBtn,
        typeExplore = typeExplore,
        showExploreParam = showExploreParam,
        exploreVar1 = exploreVar1,
        exploreVar2 = exploreVar2,
        whichShowExplore = whichShowExplore)

    analysis <- BrawSimClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

